def popular_banco():
    """
    Script para limpar a tabela de produtos e inserir dados de exemplo.
    """
    print("Iniciando script de população do banco de dados Labubu...")

    try:
        # Criar as tabelas (caso não existam)
        Base.metadata.create_all(bind=engine)
    except Exception as e:
        print(f"Erro ao criar tabelas: {e}")
        print("Verifique se a URL do banco de dados no arquivo .env está correta.")
        sys.exit(1)

    # Obter uma sessão do banco de dados
    db = SessionLocal()
    print("Conexão com o banco estabelecida.")

    try:
        # 1. LIMPAR A TABELA
        num_produtos_deletados = db.query(Produto).delete()
        db.commit()
        if num_produtos_deletados > 0:
            print(f"{num_produtos_deletados} produtos existentes foram deletados.")
        else:
            print("Tabela de produtos já estava limpa.")

        # 2. INSERIR OS NOVOS PRODUTOS
        print(f"Inserindo {len(PRODUTOS_EXEMPLO)} novos produtos Labubu...")
        
        novos_produtos = []
        for dados_produto in PRODUTOS_EXEMPLO:
            novo_produto = Produto(
                nome=dados_produto["nome"],
                descricao=dados_produto["descricao"],
                preco=dados_produto["preco"],
                imagem_url=dados_produto["imagem_url"] 
            )
            novos_produtos.append(novo_produto)
        
        db.add_all(novos_produtos)
        db.commit()
        
        print(f"\nSucesso! {len(novos_produtos)} produtos foram inseridos no catálogo.")
        
    except Exception as e:
        print(f"\nOcorreu um erro durante a transação: {e}")
        db.rollback()
    finally:
        db.close()
        print("Conexão com o banco fechada.")

if __name__ == "__main__":
    popular_banco()